import Modal from "./modal";
import { useEffect, useState } from "react";
import "../styles/forecastcard.css";

export default function Forecastcards(props) {
  const [isOpen, setIsOpen] = useState(false);
  const [divIdClicked, setDivIdClicked] = useState("");
  const [styles, setStyles] = useState({});
  let backgroundImg, textColor, textColor2, textShadow;

  function clickHandler(e) {
    setIsOpen(true);
    setDivIdClicked(e.target.id);
  }

  function close() {
    setIsOpen(false);
  }

  useEffect(() => {
    const styles = props.info.forecastday.map((day) => {
      const condition = day.day.condition.code;
      switch (condition) {
        case 1000:
          if (day.day.condition.text === "Sunny") {
            backgroundImg =
              "data:image/jpeg;base64,";
            textColor = "yellow";
            textColor2 = "yellow";
            textShadow = {
              textShadow: "1px 1px 2px blue, 0 0 1em black, 0 0 0.2em black",
            };
          } else if (day.day.condition.text === "Clear") {
            backgroundImg =
              "data:image/jpeg;base64,";

            textColor = "white";
            textColor2 = "white";
            textShadow = {
              textShadow: "1px 1px 2px blue, 0 0 1em black, 0 0 0.2em black",
            };
          }
          break;

        case 1003:
          backgroundImg =
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT2LKgYLNpCKrTQTsdDp3y_HW_6dGpdDOHnpPV2hzT-l1SkF8vuQoGUAek&s";
          textColor = "yellow";
          textColor2 = "black";
          textShadow = {
            textShadow: "1px 1px 2px blue, 0 0 1em black, 0 0 0.2em black",
          };
          break;

        case 1006:
          backgroundImg =
            "data:image/jpeg;base64,";
          textShadow = {
            textShadow: "1px 1px 2px white, 0 0 1em black, 0 0 0.2em black",
          };
          break;

        case 1009:
          backgroundImg =
            "data:image/jpeg;base64,";
          textColor = "white";
          textColor2 = "white";
          break;

        case 1030:
          backgroundImg =
            "data:image/jpeg;base64,";
          textColor2 = "white";
          textColor = "white";
          textShadow = {
            textShadow: "1px 1px 2px white, 0 0 1em black, 0 0 0.2em black",
          };
          break;

        case 1066:
        case 1210:
        case 1213:
        case 1216:
        case 1219:
        case 1255:
        case 1258:
          backgroundImg =
            "data:image/jpeg;base64,";
          textColor = "white";
          textColor2 = "black";
          break;

        case 1069:
        case 1204:
        case 1207:
        case 1249:
        case 1252:
          backgroundImg =
            "data:image/jpeg;base64,";
          break;

        case 1072:
          backgroundImg =
            "data:image/jpeg;base64,";
          break;

        case 1087:
          backgroundImg =
            "data:image/jpeg;base64,";
          break;

        case 1114:
          backgroundImg =
            "data:image/jpeg;base64,";
          break;

        case 1117:
          backgroundImg =
            "data:image/jpeg;base64,";
          break;

        case 1135:
          backgroundImg =
            "data:image/jpeg;base64,";
          break;

        case 1147:
          backgroundImg =
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTRHP_8g79wG78D2qWuNutQOLnm95MfkiXHmg&s";
          break;

        case 1153:
        case 1150:
          backgroundImg =
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR_X867sS9EZZCpMVPRQi5iUtlL_ewOHw2_DA&s";
          break;

        case 1171:
        case 1168:
          backgroundImg =
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTSEkDMfvmWGEGc-LppEIuCRvHXsYVBFsvJlA&s";
          break;

        case 1063:
        case 1180:
        case 1183:
        case 1189:
        case 1186:
        case 1240:
        case 1243:
          backgroundImg =
            "data:image/jpeg;base64,";
          textColor = "navy";
          textColor2 = "#021526";
          break;

        case 1192:
        case 1195:
          backgroundImg =
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT_vez4it7UNalVjBYpd-Uet0XoWkySlmpSLQ&s";
          break;

        case 1201:
          backgroundImg =
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQpqUZqQ0SdUZU81K8fWYTAMT1elEWrDhJt8g&s";
          break;

        case 1222:
        case 1225:
          backgroundImg =
            "data:image/jpeg;base64,";
          break;

        case 1237:
        case 1261:
        case 1264:
          backgroundImg =
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQXPZZIXNY0dwOA3T91TCUAsTk40oZ4m1E_Jg&s";
          break;

        case 1246:
          backgroundImg =
            "data:image/jpeg;base64,";
          break;

        case 1273:
        case 1276:
          backgroundImg =
            "data:image/jpeg;base64,";
          break;

        case 1279:
        case 1282:
          backgroundImg =
            "data:image/jpeg;base64,";
          break;

        default:
          backgroundImg = "";
          break;
      }

      return {
        backgroundImg,
        textColor,
        textColor2,
        textShadow,
      };
    });
    setStyles(styles);
  }, [props.info]);

  return (
    <>
      {console.log(styles)}
      {console.log(props.info)}
      {props.info.forecastday.map((day, index) => {
        return (
          <div
            className="forecast-cards"
            style={{
              position: "relative",
              backgroundImage: `url(${styles[index]?.backgroundImg})`,
              backgroundRepeat: "no-repeat",
              backgroundSize: "cover",
            }}
            key={day.date_epoch}
          >
            <div
              onClick={clickHandler}
              style={{
                margin: "0px",
                display: "block",
                cursor: "pointer",
                color: `${styles[index]?.textColor}`,
                textShadow: `${styles[index]?.textShadow}`,
              }}
              className="date-link"
              id={day.date_epoch}
            >
              {day.date}
            </div>

            <img src={day.day.condition.icon} />
            <figcaption
              style={{
                color: `${styles[index]?.textColor2}`,
                textShadow:
                  "0.2px 1px lightblue, 0 0 1em white, 0 0 0.2em white",
                fontWeight: "700",
              }}
            >
              {day.day.condition.text}
            </figcaption>
            <div style={{ position: "absolute", top: "40%", left: "25%" }}>
              <span style={{ margin: "5px", fontSize: "1.8em" }}>
                {day.day.maxtemp_f} &deg;F
              </span>
              /<span>{day.day.mintemp_f} &deg;F</span>
            </div>
            <div style={{ position: "absolute", bottom: "5px", right: "5px" }}>
              Precipitation: {day.day.daily_chance_of_rain}%
            </div>
            <Modal
              isOpen={isOpen}
              close={close}
              id={divIdClicked}
              info={props.info}
            />
          </div>
        );
      })}
    </>
  );
}
