import "../styles/weathercard.css";
import { useEffect, useState } from "react";

export default function Weathercard(props) {
  const [showMore, setShowMore] = useState(false);
  const [backgroundImg, setBackgroundImg] = useState("");
  const [textColor, setTextColor] = useState("");
  const [textColor2, setTextColor2] = useState("");
  const [textShadow, setTextShadow] = useState({});

  const condition = props.result.current.condition.code;
  useEffect(() => {
    switch (condition) {
      case 1000:
        if (props.result.current.condition.text === "Sunny") {
          setBackgroundImg(
            "data:image/jpeg;base64,"
          );
          setTextColor("yellow");
          setTextColor2("yellow");
          setTextShadow({
            textShadow: "1px 1px 2px blue, 0 0 1em black, 0 0 0.2em black",
          });
        } else if (props.result.current.condition.text === "Clear") {
          setBackgroundImg(
            "data:image/jpeg;base64,"
          );
          setTextColor("white");
          setTextColor2("white");
          setTextShadow({
            textShadow: "1px 1px 2px blue, 0 0 1em black, 0 0 0.2em black",
          });
        }
        break;

      case 1003:
        setBackgroundImg(
          "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT2LKgYLNpCKrTQTsdDp3y_HW_6dGpdDOHnpPV2hzT-l1SkF8vuQoGUAek&s"
        );
        setTextColor("yellow");
        setTextColor2("white");
        setTextShadow({
          textShadow: "1px 1px 2px blue, 0 0 1em black, 0 0 0.2em black",
        });
        break;

      case 1006:
        setBackgroundImg(
          "data:image/jpeg;base64,"
        );
        setTextShadow({
          textShadow: "1px 1px 2px white, 0 0 1em black, 0 0 0.2em black",
        });
        break;

      case 1009:
        setBackgroundImg(
          "data:image/jpeg;base64,"
        );
        setTextColor("white");
        setTextColor2("white");
        break;

      case 1030:
        setBackgroundImg(
          "data:image/jpeg;base64,"
        );
        setTextColor2("white");
        setTextColor("white");
        setTextShadow({
          textShadow: "1px 1px 2px white, 0 0 1em black, 0 0 0.2em black",
        });
        break;

      case 1066:
      case 1210:
      case 1213:
      case 1216:
      case 1219:
      case 1255:
      case 1258:
        setBackgroundImg(
          "data:image/jpeg;base64,"
        );
        setTextColor("");
        setTextColor2("white");
        break;

      case 1069:
      case 1204:
      case 1207:
      case 1249:
      case 1252:
        setBackgroundImg(
          "data:image/jpeg;base64,"
        );
        break;

      case 1072:
        setBackgroundImg(
          "data:image/jpeg;base64,"
        );
        break;

      case 1087:
        setBackgroundImg(
          "data:image/jpeg;base64,"
        );
        break;

      case 1114:
        setBackgroundImg(
          "data:image/jpeg;base64,"
        );
        break;

      case 1117:
        setBackgroundImg(
          "data:image/jpeg;base64,"
        );
        break;

      case 1135:
        setBackgroundImg(
          "data:image/jpeg;base64,"
        );
        break;

      case 1147:
        setBackgroundImg(
          "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTRHP_8g79wG78D2qWuNutQOLnm95MfkiXHmg&s"
        );
        break;

      case 1153:
      case 1150:
        setBackgroundImg(
          "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR_X867sS9EZZCpMVPRQi5iUtlL_ewOHw2_DA&s"
        );
        break;

      case 1171:
      case 1168:
        setBackgroundImg(
          "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTSEkDMfvmWGEGc-LppEIuCRvHXsYVBFsvJlA&s"
        );
        break;

      case 1063:
      case 1180:
      case 1183:
      case 1189:
      case 1186:
      case 1189:
      case 1240:
      case 1243:
        setBackgroundImg(
          "data:image/jpeg;base64,"
        );
        setTextColor("navy");
        setTextColor2("#021526");
        break;

      case 1192:
      case 1195:
        setBackgroundImg(
          "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT_vez4it7UNalVjBYpd-Uet0XoWkySlmpSLQ&s"
        );
        break;

      case 1201:
        setBackgroundImg(
          "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQpqUZqQ0SdUZU81K8fWYTAMT1elEWrDhJt8g&s"
        );
        break;

      case 1222:
      case 1225:
        setBackgroundImg(
          "data:image/jpeg;base64,"
        );
        break;

      case 1237:
      case 1261:
      case 1264:
        setBackgroundImg(
          "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQXPZZIXNY0dwOA3T91TCUAsTk40oZ4m1E_Jg&s"
        );
        break;

      case 1246:
        setBackgroundImg(
          "data:image/jpeg;base64,"
        );
        break;

      case 1273:
      case 1276:
        setBackgroundImg(
          "data:image/jpeg;base64,"
        );
        break;

      case 1279:
      case 1282:
        setBackgroundImg(
          "data:image/jpeg;base64,"
        );
        break;

      default:
        setBackgroundImg("");
        break;
    }
  }, [Weathercard]);

  return (
    <>
      <div
        className="weather-card"
        style={{
          backgroundImage: `url(${backgroundImg})`,
          backgroundRepeat: "no-repeat",
          backgroundSize: "cover",
          color: `${textColor}`,
        }}
      >
        <div className="location-area" style={textShadow}>
          <span>
            {props.location}, {props.region}, {props.country}
          </span>
        </div>
        <div className="temperature-area" style={textShadow}>
          <h2>{props.temp} &deg;F</h2>
        </div>
        <div className="condition-area">
          <img
            className="condition-icon"
            src={props.conditionIcon}
            alt={props.condition}
          />
          <figcaption style={{ color: `${textColor2}` }}>
            {props.condition}
          </figcaption>
        </div>
        <p
          style={{ color: `${textColor2}` }}
          className="show-more"
          onClick={() => {
            if (!showMore) {
              setShowMore(true);
            } else if (showMore) {
              setShowMore(false);
            }
          }}
        >
          {showMore ? "show less..." : "show more..."}
        </p>
        {showMore && (
          <div className="details" style={{ color: `${textColor2}` }}>
            <p>Wind: {props.wind} mph</p>
            <p>Wind direction: {props.windDirection}</p>
            <p>Humidity: {props.humidity}%</p>
            <p>feelslike: {props.feelslike} &deg;F</p>
            <p>Gust mph: {props.gustMph}</p>
            <p>Precipation inches: {props.precipationInches}"</p>
          </div>
        )}
        <aside className="last-updated" style={{ color: `${textColor2}` }}>
          Last Updated: {props.lastUpdated}
        </aside>
      </div>
    </>
  );
}
